import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import GetAllProducts from "../../api/products";
import "../../styles/Home.css";
import Button from "@mui/material/Button";
import Avatar from "@mui/material/Avatar";
import MdPhone from "@mui/icons-material/Phone";
import Chip from "@mui/material/Chip";
import { ImageList, ImageListItem,} from '@mui/material';
import Accordion from '../'

export const PostsList = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(GetAllProducts());
  }, [dispatch]);

  const posts = useSelector((state) => state.posts.posts);

  const renderedPosts = posts.map((post) => (
    <article className="post-excerpt" key={post._id}>
      <br />
      <div style={{ marginLeft: 20 }}>
        <Avatar alt="Avatar" />
        <p>{post.user}</p>
        <Chip
          icon={<MdPhone />}
          label="Call me"
          style={{ backgroundColor: "lightgreen" }}
        />

        <h2>{post.titre}</h2>
        <p>{post.price} $</p>
      </div>
      <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>

    <ImageListItem>
      <img
        src="https://media.istockphoto.com/photos/energy-atom-close-up-picture-id958936790?k=20&m=958936790&s=612x612&w=0&h=cKx6E0ZrJzt1cHSSLI4EN6ONXc2SnJN0mo56NWTxj3g=" alt=""
      />
       <img
        src="https://media.istockphoto.com/photos/energy-atom-close-up-picture-id958936790?k=20&m=958936790&s=612x612&w=0&h=cKx6E0ZrJzt1cHSSLI4EN6ONXc2SnJN0mo56NWTxj3g=" alt=""
      />
    </ImageListItem>

</ImageList>
<Accordion />
      <br />
      <div style={{ textAlign: "center" }}>
        <Link to={`/posts/${post._id}`} className="button muted-button">
          <Button variant="contained">
            Voir Plus

            </Button>
        </Link>
        <div>
          <br />
        </div>
      </div>
    </article>
  ));

  return (
    <section className="posts-list">
      <h2>Accueil</h2>
      <div className="HomeBox">{renderedPosts}</div>
    </section>
  );
};
