import React, { useEffect } from "react";
import "../../styles/Home.css";
import { useParams } from "react-router-dom";
import { display } from "@mui/system";

export const SinglePostPage = () => {

  const { postId } = useParams();
  console.log(postId);


  const dispacth = useDispatch();

  useEffect(() => {
    dispacth((postId));
  }, [dispacth, postId]);
  // useDispatch() is a hook that returns a function that dispatches an action.
  const posts = useSelector(state => state.posts); // useSelector() is a hook that returns the state of the store.
  const post = posts.find(post => post.id === postId); // find() is a method that returns the first element in an array that satisfies the provided testing function.
  console.log(post);





return (
  <div className="single-post-page">
    </div>
  )

}
