import {createAsyncThunk} from '@reduxjs/toolkit';
import api from './url'

const GetAllProducts = createAsyncThunk( 'products/getAll', async () => {
    const response = await api.get( '/api/data' );
    return response.data;
    }
);

export default GetAllProducts;
