import React from "react";
import "../../styles/Home.css";
import { useParams } from "react-router-dom";
import { createAsyncThunk } from "@reduxjs/toolkit";

export const SinglePostPage = () => {
  const { postId } = useParams();
  console.log(postId);

  const GetOneProducts = createAsyncThunk(
    "posts/getOneProducts",
    async (postId) => {
      const response = await fetch(`http://localhost:5000/api/data/${postId}`);
      const data = await response.json();
      return data;
    }
  );


  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(GetOneProducts(postId));
  }, [dispatch, postId]);





  return <div className="single-post-page"></div>;
};
