import React from "react";
import "../../styles/Home.css";
import { useParams } from "react-router-dom";
import { createAsyncThunk } from "@reduxjs/toolkit";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";

export const SinglePostPage = () => {
  const { postId } = useParams();
  console.log(postId);

  const GetOneProducts = createAsyncThunk(
    "posts/getOneProducts",
    async (postId) => {
      const response = await fetch(`http://localhost:5000/api/data/${postId}`);
      const data = await response.json();
      return data;
    }
  );

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(GetOneProducts(postId));
  });

  const post = useSelector((state) => state.posts.post);

  const renderPost = () => {
    if (post) {
      return (
        <div className="single-post">
          <h1>{post.titre}</h1>
        </div>
      );
    }
  };

  return <div className="single-post-page">{renderPost()}</div>;
};
