import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";

import Navbar from "./app/Navbar";
import { PostsList } from "./features/posts/PostsList";

import { SinglePostPage } from "./features/posts/SinglePostPage";
import  UsersForm  from "./features/users/UserForm";
import AddFormPost from "./features/posts/AddFormPost";

function App() {
  return (
    <Router>
      <Navbar />
      <div className="container" style={{}}>
      </div>
      <div className="App" 
      style={{backgroundImage: "url(https://images.unsplash.com/photo-1518791841217-8f162f1e1131?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80)"}}
      >
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <React.Fragment>
                <PostsList />
              </React.Fragment>
            )}
          />
          <Route exact path="/posts/:postId" component={SinglePostPage} />
          <Route exact path="/newproducts" component={AddFormPost} />
          <Route path="/users" component={UsersForm} />
          <Redirect to="/" />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
