import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import GetAllProducts from "../../api/products";
import "../../styles/Home.css";
import Button from "@mui/material/Button";
import Avatar from '@mui/material/Avatar';
import MdPhone from '@mui/icons-material/Phone';
import Chip from '@mui/material/Chip';

export const PostsList = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(GetAllProducts());
  }, [dispatch]);

  const posts = useSelector((state) => state.posts.posts);

  const renderedPosts = posts.map((post) => (
    <article className="post-excerpt" key={post._id}>
      <br />
      <div style={{ marginLeft: 20 }}>
      <Avatar alt="Remy Sharp" /> 
      <p>{post.user}</p>
      <Chip icon={<MdPhone />} label="Call me" style={{backgroundColor:"lightgreen"}} />

        <h2>{post.titre}</h2>
        <p>{post.price} $</p>
      </div>
      <img width="90%" src={post.img} alt={post.name} className="img-box" />
      <br />
      <div style={{ textAlign: "center" }}>
        <Link to={`/posts/${post.id}`} className="button muted-button">
          <Button variant="contained" >
            Voir Plus
          </Button>
        </Link>
        <div>
          <br />
        </div>


      </div>
    </article>
  ));

  return (
    <section className="posts-list">
      <h2>Accueil</h2>
      <div className="HomeBox">{renderedPosts}</div>
    </section>
  );
};
